import React,{useState,useEffect} from 'react';
import './App.css';
import Post from './Post';
import {db} from './Firebase';

function App() {

  const [posts,setPosts] = useState([]);

  useEffect(() => {
  db.collection('posts').onSnapshot(snapshot => {
    setPosts(snapshot.docs.map(doc => ({
      id: doc.id,
      post: doc.data()
    })));
  })
  },[]);
  return (
    <div className="app">
     <div className="app__header">
       <img className="app__headerImage"
         src="https://www.instagram.com/static/images/web/mobile_nav_type_logo.png/735145cfe0a4.png"
         alt=""
       />
     </div>
     <h1> Hello</h1>
     {posts.map( ({id,post}) => (
       <Post  key={id} username={post.username} caption={post.caption} imageURL={post.imageURL}/>
     ))}
     {/* <Post username="inderjeet" caption="chutiya" imageURL="https://cdn.pixabay.com/photo/2015/04/23/22/00/tree-736885_960_720.jpg"/>
     <Post username="lodu" caption="chutiya" imageURL="https://media.istockphoto.com/photos/colorful-sunset-scenery-on-open-field-picture-id1216579927"/> */}
    </div>
  );
}

export default App;
